import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { RestaurantsService } from 'src/app/services/user/restaurants/restaurants.service';

@Component({
  selector: 'app-create-restaurant',
  templateUrl: './create-restaurant.component.html',
  styleUrls: ['./create-restaurant.component.css']
})
export class CreateRestaurantComponent {
  restaurantData={
    title:'',
    details:''
  }
  token = localStorage.getItem('token')
  user:any = localStorage.getItem('user')
  editData = false
  createOne = false
  constructor(private restaurant:RestaurantsService){
    if( this.token && JSON.parse(this.user).userType=='Owner' && JSON.parse(this.user).restaurant=='' ){
      this.createOne=true
    }
    if(this.token && JSON.parse(this.user).userType=='Owner' && JSON.parse(this.user).restaurant ){
      this.editData=true
    }
  }
  handleSubmit(form : NgForm){
    if(form.valid){
      this.restaurant.addNewRestaurant({userId:JSON.parse(this.user)._id,ownerName:JSON.parse(this.user).userName,...this.restaurantData})
    }
  }

}
